select l.lesson_id, l.lesson_num, l.lesson_title, l.lesson_chapter_num, l.lesson_chapter_title, l.lesson_state, f1.file_url as file_url, f2.file_url as video_url from lesson l left join file f1 on l.lesson_file_id = f1.file_id left join file f2 on l.lesson_video_id = f2.file_id where l.lesson_course_id = #{courseId}
and 1 = 1
and l.lesson_state = 2
